            CREATE OR REPLACE SCHEMA "SALES";

CREATE TABLE "DEPTS" (
   "DEPTNO" INTEGER NOT NULL,
   "NAME" VARCHAR(128) NOT NULL,
   PRIMARY KEY ("DEPTNO")
 );

CREATE OR REPLACE INDEX "SYS$CONSTRAINT_INDEX$DEPTS_UNIQUE_NAME" ON "DEPTS" ("NAME");

CREATE TABLE "EMPS" (
   "EMPNO" INTEGER NOT NULL,
   "NAME" VARCHAR(128) NOT NULL,
   "DEPTNO" INTEGER NOT NULL,
   "GENDER" CHAR(1) DEFAULT 'M',
   "CITY" VARCHAR(128),
   "EMPID" INTEGER NOT NULL,
   "AGE" INTEGER,
   "PUBLIC_KEY" VARBINARY(50),
   "SLACKER" BOOLEAN,
   "MANAGER" BOOLEAN NOT NULL,
   PRIMARY KEY ("EMPNO","DEPTNO")
 );

CREATE OR REPLACE INDEX "EMPS_UX" ON "EMPS" ("NAME");

CREATE OR REPLACE INDEX "SYS$CONSTRAINT_INDEX$SYS$UNIQUE_KEY$EMPS$EMPID" ON "EMPS" ("EMPID");

CREATE TABLE "TEMPS" (
   "EMPNO" INTEGER NOT NULL,
   "NAME" VARCHAR(128) NOT NULL,
   "DEPTNO" INTEGER NOT NULL,
   "GENDER" CHAR(1),
   "CITY" VARCHAR(128),
   "EMPID" INTEGER DEFAULT 999 NOT NULL,
   "AGE" INTEGER,
   "PUBLIC_KEY" VARBINARY(50),
   "SLACKER" BOOLEAN,
   "MANAGER" BOOLEAN NOT NULL,
   PRIMARY KEY ("EMPNO","DEPTNO")
 );

CREATE OR REPLACE INDEX "SYS$CONSTRAINT_INDEX$SYS$PRIMARY_KEY$TEMPS" ON "TEMPS" ("DEPTNO", "EMPNO");

CREATE OR REPLACE VIEW "EMPSVIEW" AS
select empno, name from emps;

CREATE OR REPLACE VIEW "TEMPSVIEW" AS
select empno, name from temps;

CREATE OR REPLACE VIEW "JOINVIEW" AS
select depts.name as dname,emps.name as ename
from emps inner join depts
on emps.deptno=depts.deptno;

